y = explode(",",$clean["order"]);								$orderClean = array();								foreach ($orderArray as $id_immagine)				{					if ((int)$id_immagine !== 0)					{						$orderClean[] = (int)$id_immagine;					}				}								$where = "in(".implode(",",$orderClean).")";								$idOrderArray = $this->m[$this->modelName]->where(array(					"in" => array("id_immagine" => $orderClean),				))->toList("id_order")->send();								if ($this->orderBy === "immagini.id_order")				{					sort($idOrderArray);				}				else				{					rsort($idOrderArray);				}// 				echo $where;								for ($i=0; $i<count($orderClean); $i++)				{					if (isset($idOrderArray[$i]))					{						$this->m[$this->modelName]->values = array(							"id_order" => (int)$idOrderArray[$i],						);												$this->m[$this->modelName]->update((int)$orderClean[$i]);					}				}			}		}	}		public function moveup($id_immagine = null)	{		if (isset($id_immagine))		{			$clean['id_immagine'] = (int)$id_immagine;						$clean['id_page'] = (int)$this->m['ImmaginiModel']->getIdContenuto($clean['id_immagine']);			if ($clean['id_page'] !== 0)			{				$this->m['ImmaginiModel']->where(array('id_page'=>$clean['id_page']))->orderBy('id_order');				$this->m['ImmaginiModel']->moveup($clean['id_immagine']);			}		}	}	public function movedown($id_immagine = null)	{		if (isset($id_immagine))		{			$clean['id_immagine'] = (int)$id_immagine;			$clean['id_page'] = (int)$this->m['ImmaginiModel']->getIdContenuto($clean['id_immagine']);			if ($clean['id_page'] !== 0)			{				$this->m['ImmaginiModel']->where(array('id_page'=>$clean['id_page']))->orderBy('id_order');				$this->m['ImmaginiModel']->movedown($clean['id_immagine']);			}		}	}	public function erase($id_immagine = null)	{		$this->clean();		if (isset($id_immagine))		{			$clean['id_immagine'] = (int)$id_immagine;			$this->m['ImmaginiModel']->del($clean['id_immagine']);		}	}}